import React from "react";
import { useFormik } from "formik";
const validate = (values) => {
  const errors = {};
  if (!values.firstName) {
    errors.firstName = "First Name Required";
  }
  if (!values.lastName) {
    errors.lastName = "Last Name Required";
  }
  if (!values.email) {
    errors.email = "Email Required";
  } else if (
    !/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/i.test(
      values.email
    )
  ) {
    errors.email = "Invalid email format!";
  }

  if (!values.phoneNumber) {
    errors.phoneNumber = "Phone Number Required";
  } else if (
    !/^\(?([0-9]{3})\)?[-. ]?([0-9]{3})[-. ]?([0-9]{4})$/.test(
      values.phoneNumber
    )
  ) {
    errors.phoneNumber = "Invalid Phone Number";
  }
  if (!values.jobtitle) {
    errors.jobtitle = "Job Title Required";
  }
  if (!values.companyName) {
    errors.companyName = "Company Name Required";
  }
  if (!values.comment) {
    errors.comment = "comment Required";
  }
  // console.log(errors);
  return errors;
};
const NewJobCard = () => {
  const formik = useFormik({
    initialValues: {
      firstName: "",
      lastName: "",
      phoneNumber: "",
      email: "",
      jobtitle: "",
      companyName: "",
      comment: "",
    },
    validate,
    onSubmit: async (values, { resetForm }) => {
      // console.log(`Form data`, values);
    },
  });
  return (
    <div className="max-w-[1099px] max-h-[632.01px]  overflow-hidden bg-[#FFFFFF] justify-center mr-[30px] ml-[20px] my-[10px] px-[20px]  pb-[20px] rounded-[31.529px]">
      <div className="flex flex-row justify-between content-center items-center px-[50px] ">
        <div className="bg-[#F4F7FE] w-[288.28px] flex items-center h-[68.28px] mt-[31.93px]  rounded-full">
          <img
            src="/Group 8.png"
            alt="logo"
            width="42.79px"
            height="44px"
            className="content-center"
          />
          <div className=" font-secondaryFont ml-[30px]  mt-[11.51px] text-[#000000]  font-medium not-italic text-[28.09px] tracking-[-0.02em]">
            Create New Job Card
          </div>
        </div>
      </div>
      <div className="pl-[120px] pr-[96px] pt-[33.49px]">
        <form onSubmit={formik.handleSubmit}>
          <div className="flex flex-row space-x-40 pb-[16px]">
            <div className="  relative w-[350px]">
              <svg
